.button--main{position:relative;outline:0;font-family:'Open Sans',sans-serif;font-weight:400;font-size:16px;line-height:28px;display:-ms-flexbox;display:flex;-ms-flex-pack:center;justify-content:center;-ms-flex-align:center;align-items:center;max-width:129px;width:100%;padding:10px 20px;background:#32560f;border:1px solid #32560f;color:#fff;border-radius:18px;box-shadow:none}.button--arrow{position:relative;outline:0;font-family:'Open Sans',sans-serif;font-weight:400;font-size:16px;line-height:24px;color:#000;display:-ms-flexbox;display:flex;-ms-flex-pack:center;justify-content:center;-ms-flex-align:center;align-items:center;max-width:129px;width:100%;box-shadow:none;text-decoration:none}.button--arrow:before{content:"";display:block;font-weight:500;font-size:24px;line-height:32px;color:#000}.header .header__contacts .contacts__email{font-size:26px;line-height:34px}.header .header__contacts .contacts__phone{font-size:22px;line-height:28px;margin:0 0 24px}.header .header__contacts .contacts__address{margin:0 0 24px}.header .header__contacts .contacts__wrap:after{display:none}