body,html{height:100%;padding:0;margin:0}body{min-height:100vh;display:-ms-flexbox;display:flex;-ms-flex-direction:column;flex-direction:column;font-family:'Open Sans',sans-serif;font-weight:400;font-size:16px;color:#000;counter-reset:section}*{box-sizing:border-box}*,:active,:focus,:hover{outline:0}.main{padding-top:0;-ms-flex:1 0 auto;flex:1 0 auto}img{max-width:100%;height:auto}.button--main{position:relative;outline:0;font-family:'Open Sans',sans-serif;font-weight:400;font-size:16px;line-height:28px;display:-ms-flexbox;display:flex;-ms-flex-pack:center;justify-content:center;-ms-flex-align:center;align-items:center;max-width:129px;width:100%;padding:10px 20px;background:#32560f;border:1px solid #32560f;color:#fff;border-radius:18px;box-shadow:none}.button--arrow-up{position:relative;outline:0;font-family:'Open Sans',sans-serif;font-weight:400;font-size:16px;line-height:24px;color:#000;display:-ms-flexbox;display:flex;-ms-flex-pack:center;justify-content:center;-ms-flex-align:center;align-items:center;max-width:129px;width:100%;box-shadow:none;text-decoration:none}.button--arrow-up:before{content:"";display:block;font-weight:400;font-size:16px;line-height:24px;color:#000}.contacts .button--arrow-up{grid-area:up}@media (min-width:768px){.contacts .button--arrow-up{justify-self:end;-ms-flex-item-align:start;-ms-grid-row-align:start;align-self:start}}.wpcf7 form .wpcf7-response-output{margin:2em .5em 1em;padding:.2em 1em;border:2px solid #00a0d2}.wpcf7 form.init .wpcf7-response-output{display:none}.wpm-switcher-dropdown{list-style:none;margin-bottom:0;padding-left:0;position:relative}.wpm-switcher-dropdown a img,.wpm-switcher-dropdown span img{display:inline-block;margin-right:3px}.wpm-switcher-dropdown a{color:#333}.wpm-switcher-dropdown .wpm-language-dropdown{display:none;left:0;list-style:none;margin-bottom:0;margin-left:0;padding-left:0;position:absolute;top:100%}.wpm-switcher-dropdown>li{position:relative}